const ol=document.querySelector(".main__list"),popup=document.querySelector(".popup"),popupContent=document.querySelector(".popup__content"),popupClose=popup.querySelector(".popup__close");ol.addEventListener("click",e=>{if("IMG"===e.target.tagName){const p=ol.querySelectorAll(".main__item img"),t=document.createElement("img");t.src=e.target.src,t.alt="Карточка выбранного товара";const o=document.createElement("p");[...p].forEach(t=>{e.target===t&&(o.textContent=`Номер товара: ${[...p].indexOf(t)+1}`)}),popupContent.appendChild(t),popupContent.appendChild(o),popup.style.display="flex"}}),popupClose.addEventListener("click",()=>{for(popup.style.display="none";popupContent.firstChild;){if(popupContent.lastChild===popupClose)return;popupContent.removeChild(popupContent.lastChild)}});